const data = $input.first().json.body.data;
const fields = data.fields;

const getField = (key) => {
  const field = fields.find(f => f.key === key);
  return field ? field.value : null;
};

const getOptionText = (key) => {
  const field = fields.find(f => f.key === key);
  if (!field || !field.value || !field.value[0]) return '';
  const selectedId = field.value[0];
  const option = field.options.find(o => o.id === selectedId);
  return option ? option.text : '';
};

const getCheckboxesText = (key) => {
  const relatedFields = fields.filter(f => f.key.startsWith(key + '_') && f.value === true);
  return relatedFields.map(f => {
    const label = f.label.replace('\u00bfQu\u00e9 proceso te gustar\u00eda automatizar? (', '').replace(')', '');
    return label;
  });
};

const nombre = getField('question_OAGZKa') || '';
const whatsapp = getField('question_VZJaXj') || '';
const email = getField('question_PAONL1') || '';
const negocio = getField('question_EPWGel') || '';
const industria = getOptionText('question_rlPQRo');
const ciudad = getField('question_42rYRr') || '';
const instagram = getField('question_jBPrXQ') || '';
const web = getField('question_24PYze') || '';
const clientesMes = getOptionText('question_xdY7Ed');
const empleados = getOptionText('question_ZdaAXV');
const presenciaDigital = getOptionText('question_NAoxv0');
const dolorPrincipal = getOptionText('question_qbVqXO');
const serviciosDeseados = getCheckboxesText('question_QAVqpY');
const presupuesto = getOptionText('question_eBRG8x');
const timing = getOptionText('question_WAz6Xe');
const intencion = getOptionText('question_aBd1XZ');
const horario = getOptionText('question_6dNYGA') || '';
const dia = getOptionText('question_7dxYOa') || '';
const notas = getField('question_bldMX6') || '';

let puntaje = 0;
if (clientesMes.includes('M\u00e1s de 200')) puntaje += 8;
else if (clientesMes.includes('Entre 50 y 200')) puntaje += 6;
else if (clientesMes.includes('Entre 10 y 50')) puntaje += 4;
else puntaje += 2;
if (empleados.includes('M\u00e1s de 50')) puntaje += 6;
else if (empleados.includes('16 - 50')) puntaje += 5;
else if (empleados.includes('6 - 15')) puntaje += 4;
else if (empleados.includes('2 - 5')) puntaje += 3;
else puntaje += 1;
if (presupuesto.includes('$300-1000')) puntaje += 10;
else if (presupuesto.includes('$100-300')) puntaje += 6;
else if (presupuesto.includes('A\u00fan no')) puntaje += 3;
else puntaje += 0;
if (timing.includes('Ya mismo')) puntaje += 8;
else if (timing.includes('1-2 semanas')) puntaje += 6;
else if (timing.includes('1-3 meses')) puntaje += 4;
else puntaje += 2;
if (intencion.includes('consultor\u00eda')) puntaje += 8;
else puntaje += 3;

const quiereConsultoria = intencion.toLowerCase().includes('consultor\u00eda');
const noTienePresupuesto = presupuesto.includes('No tengo');
const esHot = quiereConsultoria && !noTienePresupuesto;
if (esHot && puntaje < 28) puntaje = 28;

let clasificacion = '\u2744\ufe0f COLD';
if (esHot) clasificacion = '\ud83d\udd25 HOT';

const enviarPipeline = esHot;
const listaBrevo = esHot ? 3 : 4;
const mEst = esHot ? 'Solicit\u00f3 Llamada' : 'En Nurturing';

const now = new Date();
const yy = String(now.getFullYear()).slice(-2);
const mm = String(now.getMonth() + 1).padStart(2, '0');
const shortId = Math.random().toString(36).substring(2, 6).toUpperCase();
const leadId = 'LEAD-' + yy + mm + '-' + shortId;

function findOpt(text, opts) {
  if (!text) return '';
  if (opts.includes(text)) return text;
  const m = opts.find(o => text.includes(o));
  if (m) return m;
  return text;
}

const mInd = findOpt(industria, ['Otro','Log\u00edstica','Tecnolog\u00eda','Automotriz','Construcci\u00f3n','Finanzas','Legal','Belleza','Fitness','Profesional','Turismo','Marketing','Educaci\u00f3n','Retail','Ecommerce','Inmobiliaria','Restaurante','Salud']);
const mCli = findOpt(clientesMes, ['<10','10-50','50-200','200+']);
const mEmp = findOpt(empleados, ['1','2-5','6-15','16-50','50+']);
const mPre = findOpt(presenciaDigital, ['Solo redes','Tengo web','Web+Redes+Email','Ninguna']);
const mDol = findOpt(dolorPrincipal, ['Pierdo ventas','Respondo lo mismo','No tengo organizado','Quiero escalar','Empleados en tareas repetitivas','Otro']);
const mPrs = findOpt(presupuesto, ['$100-300','$300-1000','A\u00fan no','No tengo']);
const mTim = findOpt(timing, ['Ya mismo','1-2 semanas','1-3 meses','Solo informaci\u00f3n']);
const mHor = findOpt(horario, ['Ma\u00f1ana','Tarde','Noche']);
const mDia = dia;
let mInt = '\ud83d\udce7 Solo Info Email';
if (quiereConsultoria) mInt = '\u2b50 Quiero Consultor\u00eda';
const svcOpts = ['WhatsApp','Instagram','Agendamiento','CRM','Email Marketing','Recordatorios','No estoy seguro'];
const mSvc = serviciosDeseados.map(s => { const f = svcOpts.find(o => s.toLowerCase().includes(o.toLowerCase())); return f || s; });

let igUrl = null;
if (instagram) igUrl = instagram.startsWith('http') ? instagram : 'https://instagram.com/' + instagram.replace('@','');

const notifMsg = '\ud83d\udd25 *\u00a1NUEVO LEAD HOT!*\n\n' +
  '\ud83d\udc64 *' + nombre + '* - ' + negocio + '\n' +
  '\ud83d\udcf1 ' + whatsapp + '\n' +
  '\ud83d\udce7 ' + email + '\n' +
  '\ud83c\udff7\ufe0f ' + mInd + ' | ' + mPrs + '\n' +
  '\ud83d\udcca Puntaje: ' + puntaje + ' | ID: ' + leadId + '\n\n' +
  '\ud83d\udccc *Dolor:* ' + mDol + '\n' +
  '\u23f0 *Timing:* ' + mTim + '\n' +
  '\ud83d\udcc5 *Disponibilidad:* ' + mHor + ' - ' + mDia + '\n\n' +
  '\u2705 Ya est\u00e1 en Pipeline';

const props = {
  'Nombre': { title: [{ text: { content: nombre } }] },
  'Email': { email: email || null },
  'WhatsApp': { phone_number: whatsapp || null },
  'Negocio': { rich_text: [{ text: { content: negocio } }] },
  'Industria': { select: { name: mInd } },
  'Pa\u00eds/Ciudad': { rich_text: [{ text: { content: ciudad } }] },
  'Clientes/mes': { select: { name: mCli } },
  'Empleados': { select: { name: mEmp } },
  'Presencia Digital': { select: { name: mPre } },
  'Dolor Principal': { select: { name: mDol } },
  'Servicios Deseados': { multi_select: mSvc.map(s => ({ name: s })) },
  'Presupuesto': { select: { name: mPrs } },
  'Timing': { select: { name: mTim } },
  'Intenci\u00f3n': { select: { name: mInt } },
  'Disponibilidad Horario': { select: { name: mHor } },
  'Disponibilidad D\u00eda': { rich_text: [{ text: { content: mDia } }] },
  'Puntaje': { number: puntaje },
  'Estado': { select: { name: mEst } },
  'Enviado a Pipeline': { checkbox: enviarPipeline },
  'Fuente': { select: { name: 'Formulario Web' } },
  'ID Lead': { rich_text: [{ text: { content: leadId } }] }
};
if (igUrl) props['Instagram'] = { url: igUrl };
if (web) props['Web'] = { url: web };
if (notas) props['Notas Adicionales'] = { rich_text: [{ text: { content: notas } }] };

const notionBody = { parent: { database_id: '309668bb-053e-807a-a410-d432654f58fd' }, properties: props };

const pipelineBody = { parent: { database_id: '309668bb-053e-80d8-a3eb-ea165a96ff1a' }, properties: {
  'Nombre': { title: [{ text: { content: nombre } }] },
  'Negocio': { rich_text: [{ text: { content: negocio } }] },
  'Estado': { select: { name: '\u2b50 Llamada Solicitada' } },
  'Clasificaci\u00f3n': { select: { name: '\ud83d\udd25 HOT' } },
  'WhatsApp': { phone_number: whatsapp || null },
  'Email': { email: email || null },
  'Industria': { select: { name: mInd } },
  'Puntaje': { number: puntaje },
  'ID Cliente': { rich_text: [{ text: { content: leadId } }] }
}};

const brevoBody = { email: email, attributes: { NOMBRE: nombre, NEGOCIO: negocio, WSAPP: whatsapp, INDUSTRIA: mInd, PUNTAJE: puntaje, INTENCION: mInt }, listIds: [listaBrevo], updateEnabled: true };

return [{ json: { nombre, email, puntaje, enviarPipeline, leadId, notifMsg, notionBody, pipelineBody, brevoBody } }];

