const telefono = $('Extraer Datos').first().json.telefono;
const mensaje = $('Extraer Datos').first().json.mensaje;
const mensaje_original = $('Extraer Datos').first().json.mensaje_original;
const nombre_wa = $('Extraer Datos').first().json.nombre_wa;
const body_original = $('Extraer Datos').first().json.body_original;

let filas = [];
try { filas = $input.all().map(i => i.json); } catch(e) { filas = []; }

const telLimpio = String(telefono).trim();
const encontrado = filas.find(f => String(f.telefono || '').trim() === telLimpio);

if (encontrado && encontrado.demo_activa) {
  return [{ 
    json: { 
      existe: true, 
      telefono: telLimpio, 
      mensaje,
      mensaje_original,
      nombre_wa,
      body_original,
      demo_activa: String(encontrado.demo_activa || '').trim().toLowerCase(),
      paso: String(encontrado.paso || '').trim().toLowerCase(),
      datos_extra: encontrado.datos_extra || ''
    } 
  }];
} else {
  return [{ 
    json: { 
      existe: false, 
      telefono: telLimpio, 
      mensaje,
      mensaje_original,
      nombre_wa,
      body_original,
      demo_activa: '',
      paso: 'nuevo',
      datos_extra: ''
    } 
  }];
}

