const item = $input.first();
const data = item.json;

// Soporte para ambos formatos: {query: {...}} y campos planos
const query = (data.query && typeof data.query === 'object') ? data.query : data;
const especialistaRaw = (query.especialista || data.especialista || '').toLowerCase();

// Normalizar acentos
const normalizar = (s) => s.normalize('NFD').replace(/[\u0300-\u036f]/g, '');
const especialista = normalizar(especialistaRaw);

const ESPECIALISTAS = {
  garcia: 4857668,
  lopez: 4857681,
  martinez: 4857683,
};

let eventTypeId = null;
for (const [key, id] of Object.entries(ESPECIALISTAS)) {
  if (especialista.includes(key)) {
    eventTypeId = id;
    break;
  }
}

if (!eventTypeId) {
  return [{ json: { error: `Especialista no reconocido: ${especialistaRaw}`, confirmado: false } }];
}

return [{
  json: {
    eventTypeId,
    startTime: query.startTime || data.startTime || '',
    nombre: query.nombre || data.nombre || '',
    email: query.email || data.email || '',
    especialista: especialistaRaw
  }
}];

const item = $input.first();
const data = item.json;

if (data.error) {
  return [{ json: data }];
}

// Cal.com v2 booking response
const booking = data.data || data;
return [{
  json: {
    confirmado: true,
    bookingId: booking.id || booking.uid || '',
    titulo: booking.title || '',
    inicio: booking.start || '',
    fin: booking.end || '',
    estado: booking.status || 'accepted',
    paciente: booking.attendees?.[0]?.name || '',
    email: booking.attendees?.[0]?.email || ''
  }
}];

